package com.alg.pack;/** * @author Yasin Zhang */public class CompletePack {    public int solution(int V, int N, int[] C, int[] W) {        int[] F = new int[V+1];        for (int i = 1; i <= N; i++) {            completePack(F, C[i-1], W[i-1], V);        }        return F[V];    }    public static void completePack(int[] F, int C, int W, int V) {        for (int v = C; v <= V; v++) {            F[v] = Math.max(F[v], F[v-C] + W);        }    }}