package com.alg.pack;/** * @author Yasin Zhang */public class ZeroOnePack {    public int solution(int V, int N, int[] C, int[] W) {        int[] F = new int[V+1];        for (int i = 1; i <= N; i++) {            zeroOnePack(F, C[i-1], W[i-1], V);        }        return F[V];    }    public static void zeroOnePack(int[] F, int C, int W, int V) {        for (int v = V; v >= C; v--) {            F[v] = Math.max(F[v], F[v-C] + W);        }    }}